package com.FactoryModel.excemple;import com.FactoryModel.excemple.context.StrategyContext;import com.FactoryModel.excemple.factory.StrategyFactoryImpl;import com.FactoryModel.excemple.strategy.StrategyInteface;/** * Created by admin on 2017/4/20. */public class Excemple {    public enum KindEnum {        STUDENT(0, "student"),        TEACHHER(1, "teacher"),        EMPLOYEE(2, "employee"),;        public static String getDesc(int type){            for (KindEnum s : KindEnum.values()){                if (s.getType() == type){                    return s.getDesc();                }            }            return null;        }        private Integer type;        private String desc;        KindEnum(Integer type, String desc) {            this.type = type;            this.desc = desc;        }        public Integer getType() {            return type;        }        public void setType(Integer type) {            this.type = type;        }        public String getDesc() {            return desc;        }        public void setDesc(String desc) {            this.desc = desc;        }    }    public static void main(String[] args) {        for (int i =0;i<4;i++) {            StrategyInteface inteface = new StrategyContext().run(i);            System.out.println(inteface.solution(i, KindEnum.getDesc(i)));        }    }}