package com.FactoryModel.excemple.strategy;import java.util.HashMap;import java.util.Map;/** * Created by admin on 2017/4/20. */public class TeacherStrategyImpl implements StrategyInteface {    private Integer type;    public TeacherStrategyImpl(Integer type) {        this.type = type;    }    @Override    public Map<Object, Object> solution(Integer type, String desc) {        Map<Object, Object> map = new HashMap();        map.put(type, desc);        map.put(this.getClass().getName(), this.getClass().getClassLoader());        return map;    }}