package create.sql;import java.io.*;import java.text.SimpleDateFormat;import java.util.Date;/** * Created by admin on 2017/4/25. */public class CreateCouponTemplate {    public static void main(String[] args) {        StringBuffer sb = new StringBuffer();        for (City c : City.values()){            for (Category category : Category.values()){                sb.append(c.cityCode+","+category.category+"\n");            }        }        System.out.println(sb.toString());       BufferedWriter out = null;        try {            String time = new SimpleDateFormat("yyyy-MM-dd").format(new Date());            File file = new File("/Users/yehao/Desktop/createSql_"+ time+".sql");            if (file.exists()){                file.delete();                file.createNewFile();            }            out = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(file, true)));            out.write(sb + "\r\n");            out.flush();            out.close();        } catch (IOException e) {            e.printStackTrace();        }    }    enum City{        BEIJING("110100", "北京市"),        TIANJIN("120100", "天津市"),        SHANGHAI("310100", "上海市"),        NANJING("320100", "南京市"),        WUHAN("420100", "武汉市"),        HANGZHOU("330100", "杭州市"),        GUANGZHOU("440100", "广州市"),        SHENZHEN("440300", "深圳市"),        CHONGQING("500100", "重庆市"),        CHENGDU("510100", "成都市"),        XIAN("610100", "西安市"),        ;        City(String cityCode, String cityName) {            this.cityCode = cityCode;            this.cityName = cityName;        }        private String cityCode;        public String getCityName() {            return cityName;        }        public void setCityName(String cityName) {            this.cityName = cityName;        }        private String cityName;        public String getCityCode() {            return cityCode;        }        public void setCityCode(String cityCode) {            this.cityCode = cityCode;        }    }    enum Category{        MEIJIA("tag_mei_jia", "美甲"),        MEIRONG("tag_mei_rong", "美容"),        MEIJIE("tag_mei_jie", "美睫"),        WEIZHENG("tag_wei_zheng", "微整"),        OTHER("other", "其他");        Category(String category, String name) {            this.category = category;            this.name = name;        }        private String category ;        private String name;        public String getCategory() {            return category;        }        public void setCategory(String category) {            this.category = category;        }        public String getName() {            return name;        }        public void setName(String name) {            this.name = name;        }    }}